import React, { useState, useEffect } from "react";
import axios from "axios";

const WhaleTransactions = () => {
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [period, setPeriod] = useState(7); // 7 zile
  const [selectedBlockchain, setSelectedBlockchain] = useState("ethereum");

  useEffect(() => {
    // Funcția pentru a obține tranzacțiile mari
    const fetchTransactions = async () => {
      try {
        const apiKey = "RP1AAGBP2YNUWFTAFP6KWT7GRRKC5BG5MM"; // Înlocuiește cu cheia ta de API de la Etherscan
        const endpoint = `https://api.etherscan.io/api`;

        // Apel API pentru a obține tranzacțiile
        const response = await axios.get(endpoint, {
          params: {
            module: "account",
            action: "txlist",
            address: "0xYourAddressHere", // Adresa care dorești să o monitorizezi
            startblock: 0,
            endblock: 99999999,
            sort: "desc",
            apikey: apiKey,
          },
        });

        // Filtrăm tranzacțiile mari, mai mari de 100,000 USD (în ETH)
        const largeTransactions = response.data.result.filter((transaction) => {
          const amountInEth = parseFloat(transaction.value) / Math.pow(10, 18); // Convertim valoarea în ETH
          return amountInEth > 100; // Tranzacții mai mari de 100 ETH
        });

        setTransactions(largeTransactions);
      } catch (error) {
        console.error("Eroare la obținerea tranzacțiilor:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchTransactions();
  }, [period, selectedBlockchain]); // Refacerea cererii când se schimbă perioada sau blockchain-ul

  if (loading) {
    return <div>Loading transactions...</div>;
  }

  return (
    <div>
      <h2>Whale Transactions (Over 100 ETH)</h2>

      {/* Selectare blockchain */}
      <div>
        <label htmlFor="blockchain">Select Blockchain:</label>
        <select
          id="blockchain"
          value={selectedBlockchain}
          onChange={(e) => setSelectedBlockchain(e.target.value)}
        >
          <option value="ethereum">Ethereum</option>
          <option value="binancecoin">Binance Coin (BSC)</option>
          <option value="bitcoin">Bitcoin</option>
          {/* Adaugă opțiuni suplimentare pentru alte blockchain-uri */}
        </select>
      </div>

      {/* Selectare perioadă */}
      <div>
        <label htmlFor="period">Select Period (Days):</label>
        <input
          id="period"
          type="number"
          value={period}
          onChange={(e) => setPeriod(e.target.value)}
          min="1"
        />
      </div>

      <table>
        <thead>
          <tr>
            <th>Timestamp</th>
            <th>Amount (ETH)</th>
            <th>Blockchain</th>
          </tr>
        </thead>
        <tbody>
          {transactions.length === 0 ? (
            <tr>
              <td colSpan="3">No large transactions found.</td>
            </tr>
          ) : (
            transactions.map((transaction, index) => (
              <tr key={index}>
                <td>
                  {new Date(transaction.timeStamp * 1000).toLocaleString()}
                </td>
                <td>{parseFloat(transaction.value) / Math.pow(10, 18)} ETH</td>
                <td>{selectedBlockchain}</td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
};

export default WhaleTransactions;
