import React, { useEffect, useState } from "react";

const AltcoinSeasonIndex = () => {
  const [percentage, setPercentage] = useState(0);
  const [historicalData, setHistoricalData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchAltcoinSeasonIndex = async () => {
      try {
        setLoading(true);
        const response = await fetch(
          "https://api.blockchaincenter.net/api/v3/altcoin-season-index"
        );
        const data = await response.json();

        setPercentage(data.altcoin_season_index);
        setHistoricalData({
          yesterday: data.history.yesterday,
          lastWeek: data.history.lastWeek,
          lastMonth: data.history.lastMonth,
          yearlyHigh: data.history.yearlyHigh,
          yearlyLow: data.history.yearlyLow,
        });
      } catch (error) {
        console.error("Error fetching altcoin season index:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchAltcoinSeasonIndex();
  }, []);

  if (loading)
    return (
      <div className="text-center text-gray-700 text-lg">
        Loading Altcoin Season Index...
      </div>
    );

  const barColor =
    percentage >= 80
      ? "bg-green-600"
      : percentage >= 60
      ? "bg-green-400"
      : percentage >= 40
      ? "bg-yellow-400"
      : percentage >= 20
      ? "bg-orange-400"
      : "bg-red-500";

  return (
    <div className="p-4 bg-white dark:bg-gray-900 rounded-lg shadow-lg">
      <h3 className="text-xl font-bold text-gray-800 dark:text-white text-center">
        Altcoin Season Index: {percentage}%
      </h3>
      <div className="relative w-full bg-gray-300 h-4 rounded-full my-4 overflow-hidden">
        <div
          className={`h-4 rounded-full transition-all duration-500 ${barColor}`}
          style={{ width: `${percentage}%` }}
        ></div>
      </div>
      {historicalData && (
        <div className="text-center text-gray-700 dark:text-gray-300">
          <p>
            Yesterday:{" "}
            <span className="font-semibold">{historicalData.yesterday}%</span>
          </p>
          <p>
            Last Week:{" "}
            <span className="font-semibold">{historicalData.lastWeek}%</span>
          </p>
          <p>
            Last Month:{" "}
            <span className="font-semibold">{historicalData.lastMonth}%</span>
          </p>
          <p>
            Yearly High:{" "}
            <span className="font-semibold">{historicalData.yearlyHigh}%</span>
          </p>
          <p>
            Yearly Low:{" "}
            <span className="font-semibold">{historicalData.yearlyLow}%</span>
          </p>
        </div>
      )}
    </div>
  );
};

export default AltcoinSeasonIndex;
