import React from "react";
import { motion } from "framer-motion";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from "recharts";

const AltcoinChart = ({ coin, onClose }) => {
  // Simulăm niște date istorice pentru grafic
  const generateHistoricalData = () => {
    const data = [];
    let price = 100; // Prețul inițial (simulat)
    for (let i = 0; i < 30; i++) {
      data.push({
        day: `Day ${i + 1}`,
        price: price.toFixed(2),
      });
      price += (Math.random() - 0.5) * 10; // Schimbare aleatorie a prețului
    }
    return data;
  };

  const chartData = generateHistoricalData();

  return (
    <motion.div
      initial={{ opacity: 0, x: 50 }}
      animate={{ opacity: 1, x: 0 }}
      exit={{ opacity: 0, x: 50 }}
      className="fixed top-0 right-0 h-full w-1/3 bg-white dark:bg-gray-800 shadow-lg p-6"
    >
      <button onClick={onClose} className="text-gray-700 dark:text-white mb-4">
        Close
      </button>
      <h2 className="text-2xl font-bold text-gray-900 dark:text-white mb-4">
        {coin.name}
      </h2>
      <p className="text-gray-700 dark:text-gray-300 mb-6">
        Price Change: {coin.priceChange > 0 ? "+" : ""}
        {coin.priceChange.toFixed(2)}%
      </p>
      <img
        src={coin.image}
        alt={coin.name}
        className="w-16 h-16 rounded-full mb-6"
      />

      {/* Graficul */}
      <div className="w-full h-64">
        <ResponsiveContainer width="100%" height="100%">
          <LineChart data={chartData}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="day" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Line
              type="monotone"
              dataKey="price"
              stroke="#8884d8"
              activeDot={{ r: 8 }}
            />
          </LineChart>
        </ResponsiveContainer>
      </div>
    </motion.div>
  );
};

export default AltcoinChart;
