import React, { useState, useEffect } from "react";
import NightToggle from "./NightToggle"; // Importă componenta NightToggle

function Header({ setActiveTab }) {
  const [cryptoData, setCryptoData] = useState({
    bitcoinDominance: 0,
    bitcoinDominanceChange: 0,
    ethereumDominance: 0,
    ethereumDominanceChange: 0,
    totalVolume24h: 0,
    volumeChange24h: 0,
    totalMarketCap: 0,
    marketCapChange24h: 0,
  });

  useEffect(() => {
    // Funcție pentru a obține datele de la CryptoCompare
    const fetchCryptoCompareData = async () => {
      try {
        const response = await fetch(
          "https://min-api.cryptocompare.com/data/top/mktcapfull?limit=10&tsym=USD"
        );
        const data = await response.json();

        // Extrage datele pentru Bitcoin și Ethereum
        const bitcoinData = data.Data.find(
          (coin) => coin.CoinInfo.Name === "BTC"
        );
        const ethereumData = data.Data.find(
          (coin) => coin.CoinInfo.Name === "ETH"
        );

        // Calculează dominanța și schimbările procentuale
        const totalMarketCap =
          bitcoinData.RAW.USD.MKTCAP + ethereumData.RAW.USD.MKTCAP;
        const bitcoinDominance =
          (bitcoinData.RAW.USD.MKTCAP / totalMarketCap) * 100;
        const ethereumDominance =
          (ethereumData.RAW.USD.MKTCAP / totalMarketCap) * 100;

        // Schimbări procentuale
        const bitcoinDominanceChange = bitcoinData.RAW.USD.CHANGEPCT24HOUR;
        const ethereumDominanceChange = ethereumData.RAW.USD.CHANGEPCT24HOUR;

        // Actualizează state-ul cu noile date
        setCryptoData({
          bitcoinDominance,
          bitcoinDominanceChange,
          ethereumDominance,
          ethereumDominanceChange,
          totalVolume24h:
            bitcoinData.RAW.USD.VOLUME24HOUR +
            ethereumData.RAW.USD.VOLUME24HOUR,
          volumeChange24h:
            (bitcoinData.RAW.USD.CHANGEPCT24HOUR +
              ethereumData.RAW.USD.CHANGEPCT24HOUR) /
            2,
          totalMarketCap,
          marketCapChange24h:
            (bitcoinData.RAW.USD.CHANGEPCT24HOUR +
              ethereumData.RAW.USD.CHANGEPCT24HOUR) /
            2,
        });
      } catch (error) {
        console.error("Error fetching CryptoCompare data:", error);
      }
    };

    fetchCryptoCompareData();
  }, []);

  return (
    <header className="bg-gradient-to-r from-gray-800 to-gray-900 text-white py-6 shadow-lg">
      <div className="px-4 sm:px-6 lg:px-8">
        {/* Titlul site-ului, navbar-ul și NightToggle pe aceeași linie */}
        <div className="flex items-center justify-between">
          {/* Partea din stânga: Titlul și navbar-ul */}
          <div className="flex items-center">
            {/* Titlul site-ului */}
            <h1 className="text-2xl font-extrabold tracking-tight text-transparent bg-clip-text bg-gradient-to-r from-teal-300 to-green-400">
              SentimentX
            </h1>

            {/* Navbar */}
            <nav className="flex space-x-8 text-lg font-semibold ml-8">
              <button
                className="text-white hover:text-teal-300 transition duration-300"
                onClick={() => setActiveTab("news")}
              >
                News
              </button>

              <button
                className="text-white hover:text-teal-300 transition duration-300"
                onClick={() => setActiveTab("altcoin")}
              >
                Altcoin Season Index
              </button>

              <button
                className="text-white hover:text-teal-300 transition duration-300"
                onClick={() => setActiveTab("sentiment")}
              >
                Sentiment Trend
              </button>

              <button
                className="text-white hover:text-teal-300 transition duration-300"
                onClick={() => setActiveTab("whaleTransactions")}
              >
                Whale Transactions
              </button>
            </nav>
          </div>

          {/* Partea din dreapta: NightToggle */}
          <div className="flex items-center">
            <NightToggle />
          </div>
        </div>

        {/* Linie orizontală */}
        <hr className="border-gray-700 my-4" />

        {/* Informații despre crypto */}
        <div className="flex space-x-8 text-sm font-medium text-gray-300">
          {/* Bitcoin Dominance */}
          <span>
            BTC Dominance: {cryptoData.bitcoinDominance.toFixed(2)}% (
            <span
              className={
                cryptoData.bitcoinDominanceChange >= 0
                  ? "text-green-400"
                  : "text-red-400"
              }
            >
              {cryptoData.bitcoinDominanceChange.toFixed(2)}%
            </span>
            )
          </span>

          {/* Ethereum Dominance */}
          <span>
            ETH Dominance: {cryptoData.ethereumDominance.toFixed(2)}% (
            <span
              className={
                cryptoData.ethereumDominanceChange >= 0
                  ? "text-green-400"
                  : "text-red-400"
              }
            >
              {cryptoData.ethereumDominanceChange.toFixed(2)}%
            </span>
            )
          </span>

          {/* 24h Volume */}
          <span>
            24h Vol: ${cryptoData.totalVolume24h.toLocaleString()} (
            <span
              className={
                cryptoData.volumeChange24h >= 0
                  ? "text-green-400"
                  : "text-red-400"
              }
            >
              {cryptoData.volumeChange24h.toFixed(2)}%
            </span>
            )
          </span>

          {/* Market Cap */}
          <span>
            Market Cap: ${cryptoData.totalMarketCap.toLocaleString()} (
            <span
              className={
                cryptoData.marketCapChange24h >= 0
                  ? "text-green-400"
                  : "text-red-400"
              }
            >
              {cryptoData.marketCapChange24h.toFixed(2)}%
            </span>
            )
          </span>
        </div>
      </div>
    </header>
  );
}

export default Header;
