// src/App.js
import React, { useState } from "react";
import Header from "./components/Header";
import SentimentChart from "./components/SentimentChart"; // Importă componenta SentimentChart
import NewsCard from "./components/NewsCard";
import AltcoinSeason from "./components/AltcoinSeason";
import Footer from "./components/Footer";

function App() {
  const [isNightMode, setIsNightMode] = useState(true); // Setăm default la true pentru Night Mode

  const toggleNightMode = () => {
    setIsNightMode((prevMode) => !prevMode); // Toggle între Night Mode și Day Mode
  };

  return (
    <div
      className={`App ${
        isNightMode ? "bg-gray-900 text-white" : "bg-gray-100 text-black"
      } min-h-screen`}
    >
      <Header isNightMode={isNightMode} onToggleNightMode={toggleNightMode} />
      {/* Transmite isNightMode către SentimentChart */}
      <SentimentChart isNightMode={isNightMode} />
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
        <AltcoinSeason />
        <NewsCard />
      </main>
      <Footer />
    </div>
  );
}

export default App;
