// src/components/SentimentChart.js
import React, { useEffect, useState } from "react";
import { Line } from "react-chartjs-2";
import axios from "axios";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";
import { format } from "date-fns";

// Înregistrăm componentele Chart.js
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

function SentimentChart() {
  const [sentimentData, setSentimentData] = useState({
    labels: [],
    datasets: [],
  });

  const fetchSentimentData = async () => {
    try {
      const response = await axios.get(
        "https://api.alternative.me/fng/?limit=365&format=json"
      );

      const sentimentScores = response.data.data.map((item) =>
        parseInt(item.value)
      );
      const sentimentTimestamps = response.data.data.map((item) =>
        format(new Date(item.timestamp * 1000), "MMM dd, yyyy")
      );

      // Inversăm datele pentru a avea cea mai recentă dată în dreapta
      const reversedTimestamps = sentimentTimestamps.reverse();
      const reversedScores = sentimentScores.reverse();

      // Funcție pentru alegerea culorii liniei în funcție de valoare
      const getColor = (value) => {
        if (value < 30) return "red"; // Frică extremă
        if (value < 60) return "yellow"; // Neutru
        return "green"; // Lăcomie
      };

      setSentimentData({
        labels: reversedTimestamps,
        datasets: [
          {
            label: "Sentiment Trend (Fear & Greed Index)",
            data: reversedScores,
            fill: false,
            borderColor: (ctx) => {
              if (!ctx.chart) return "black"; // Default
              const index = ctx.dataIndex;
              return getColor(reversedScores[index]);
            },
            segment: {
              borderColor: (ctx) => {
                if (!ctx.p0 || !ctx.p1) return "black";
                return getColor(ctx.p1.raw);
              },
            },
            tension: 0.1,
            pointBackgroundColor: (ctx) =>
              getColor(reversedScores[ctx.dataIndex]),
            pointBorderColor: (ctx) => getColor(reversedScores[ctx.dataIndex]),
            pointRadius: 4,
          },
        ],
      });
    } catch (error) {
      console.error("Error fetching sentiment data:", error);
    }
  };

  useEffect(() => {
    fetchSentimentData();
  }, []);

  const options = {
    responsive: true,
    scales: {
      x: {
        title: { display: true, text: "Date" },
        ticks: { autoSkip: true, maxTicksLimit: 10, maxRotation: 45 },
      },
      y: {
        title: { display: true, text: "Fear and Greed Index" },
        ticks: { min: 0, max: 100, stepSize: 10 },
      },
    },
  };

  return (
    <section className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-16">
      <h2 className="text-3xl font-semibold text-gray-800">
        Market Sentiment (Fear and Greed Index)
      </h2>
      <div className="mt-6">
        <Line data={sentimentData} options={options} />
      </div>
    </section>
  );
}

export default SentimentChart;
