// src/components/NightModeToggle.js
import React from "react";

const NightModeToggle = ({ onToggle }) => {
  return (
    <button
      onClick={() => onToggle((prevMode) => !prevMode)} // Schimbă tema la click
      className="absolute top-4 right-4 p-2 bg-gray-600 rounded-full hover:bg-gray-700"
    >
      {/* Icona pentru Night Mode */}
      <svg
        xmlns="http://www.w3.org/2000/svg"
        className="w-6 h-6 text-white"
        fill="none"
        viewBox="0 0 24 24"
        stroke="currentColor"
      >
        <path
          strokeLinecap="round"
          strokeLinejoin="round"
          strokeWidth="2"
          d="M12 3v2m0 14v2m9-9h-2m-14 0H3m15.364-6.364l-1.414 1.414M6.636 6.636L5.222 5.222m13.414 13.414l-1.414-1.414M6.636 17.364L5.222 18.778m7.364-7.364a5 5 0 100 10 5 5 0 000-10z"
        />
      </svg>
    </button>
  );
};

export default NightModeToggle;
