import React, { useEffect, useState } from "react";
import { FaEthereum, FaBitcoin } from "react-icons/fa"; // Iconițe pentru Ethereum și Bitcoin
import { formatDistanceToNow } from "date-fns"; // Pentru a formata timpul (Age)

const WhaleTransactions = () => {
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchWhaleTransactions = async () => {
      const API_KEY_ETH = "RP1AAGBP2YNUWFTAFP6KWT7GRRKC5BG5MM"; // Replace with your Etherscan API key for Ethereum
      const API_KEY_BSC = "YOUR_BSCSCAN_API_KEY"; // Replace with your BscScan API key for Binance Smart Chain

      // List of wallets and associated exchanges
      const wallets = [
        {
          name: "Binance",
          address: "0x742d35Cc6634C0532925a3b844Bc454e4438f44e",
          blockchain: "ETH", // Ethereum
          apiKey: API_KEY_ETH,
        },
        {
          name: "Coinbase",
          address: "0x5d69c5e4727f0d2f6b8a64358d9e4c8981b9bb6f",
          blockchain: "ETH", // Ethereum
          apiKey: API_KEY_ETH,
        },
        {
          name: "PancakeSwap",
          address: "0x7a80d8d3d77222b9f6889b5e8b6a7ef5d7f086df",
          blockchain: "BSC", // Binance Smart Chain
          apiKey: API_KEY_BSC,
        },
        {
          name: "FTX",
          address: "0x9e8cb2fae327d2a22b5d1e9db1734f97511fba9f",
          blockchain: "ETH", // Ethereum
          apiKey: API_KEY_ETH,
        },
      ];

      const allTransactions = [];

      for (let wallet of wallets) {
        const { address, blockchain, apiKey, name } = wallet;
        const url =
          blockchain === "ETH"
            ? `https://api.etherscan.io/api?module=account&action=txlist&address=${address}&startblock=0&endblock=99999999&page=1&offset=10&sort=desc&apikey=${apiKey}`
            : `https://api.bscscan.com/api?module=account&action=txlist&address=${address}&startblock=0&endblock=99999999&page=1&offset=10&sort=desc&apikey=${apiKey}`;

        try {
          const response = await fetch(url);
          const data = await response.json();
          console.log(data); // Check the API response

          if (data.status === "1") {
            // Filter transactions larger than 1 ETH / BNB
            const filteredTransactions = data.result.filter(
              (tx) => parseFloat(tx.value) > 10 ** 18
            ); // > 1 ETH / BNB

            const transactionsWithData = filteredTransactions.map((tx) => ({
              hash: tx.hash,
              from: tx.from,
              to: tx.to,
              value: tx.value / 10 ** 18,
              blockchain,
              exchange: name,
              date: new Date(tx.timeStamp * 1000).toLocaleString(), // Convert timestamp to human-readable date/time
              fee: tx.gasUsed ? (tx.gasUsed * tx.gasPrice) / 10 ** 18 : "N/A", // Transaction Fee (if available)
              block: tx.blockNumber,
              age: formatDistanceToNow(new Date(tx.timeStamp * 1000)), // Time since transaction
              icon:
                blockchain === "ETH" ? (
                  <FaEthereum className="text-blue-500 text-xl" />
                ) : (
                  <FaBitcoin className="text-yellow-500 text-xl" />
                ),
            }));

            allTransactions.push(...transactionsWithData);
          } else {
            console.error(`No transactions found for wallet: ${name}`);
          }
        } catch (error) {
          console.error("Error fetching transactions:", error);
        }
      }

      setTransactions(allTransactions);
      setLoading(false);
    };

    fetchWhaleTransactions();
  }, []);

  return (
    <div className="container mx-auto p-6">
      <h2 className="text-3xl font-bold text-center text-gray-800 mb-6">
        Whale Transactions
      </h2>
      {loading ? (
        <p className="text-center text-xl text-gray-600">
          Loading transactions...
        </p>
      ) : (
        <div className="space-y-6">
          {" "}
          {/* Adding space between each card */}
          {transactions.length > 0 ? (
            transactions.map((tx, index) => (
              <div key={index} className="bg-white rounded-lg shadow-lg p-6">
                <div className="flex items-center mb-4">
                  {/* Imagine simbolică pentru tranzacție */}
                  <div className="w-12 h-12 bg-gray-100 rounded-full flex justify-center items-center mr-4">
                    {tx.icon}
                  </div>
                  <div className="flex-1">
                    <h3 className="text-xl font-semibold text-gray-700">
                      {tx.exchange}
                    </h3>
                    <p className="text-gray-600">
                      <strong>From:</strong> {tx.from}
                    </p>
                    <p className="text-gray-600">
                      <strong>To:</strong> {tx.to}
                    </p>
                    <p className="text-gray-600">
                      <strong>Amount:</strong> {tx.value}{" "}
                      {tx.blockchain === "ETH" ? "ETH" : "BNB"}
                    </p>
                    <p className="text-gray-500">
                      <strong>Transaction Fee:</strong> {tx.fee} ETH
                    </p>
                    <p className="text-gray-500">
                      <strong>Block:</strong> {tx.block}
                    </p>
                    <p className="text-gray-500">
                      <strong>Age:</strong> {tx.age} ago
                    </p>
                    <p className="text-gray-500">
                      <strong>Transaction Hash:</strong>{" "}
                      <a
                        href={`https://etherscan.io/tx/${tx.hash}`}
                        className="text-blue-600"
                        target="_blank"
                        rel="noopener noreferrer"
                      >
                        {tx.hash}
                      </a>
                    </p>
                  </div>
                </div>
              </div>
            ))
          ) : (
            <p className="text-center text-xl text-gray-600">
              No large transactions available at the moment.
            </p>
          )}
        </div>
      )}
    </div>
  );
};

export default WhaleTransactions;
