// src/App.js
import React from "react";
import Header from "./components/Header";
import SentimentChart from "./components/SentimentChart"; // Rămâne activ
import NewsCard from "./components/NewsCard"; // Nu mai facem getNews aici
import Footer from "./components/Footer";

function App() {
  return (
    <div className="App bg-gray-100 min-h-screen">
      <Header />

      {/* SentimentChart rămâne plasat aici */}
      <SentimentChart />

      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
        {/* Aici vom plasa NewsCard fără a transmite props, pentru că acesta se ocupă de API intern */}
        <NewsCard />
      </main>

      <Footer />
    </div>
  );
}

export default App;
