// src/components/AltcoinSeason.js
import React, { useState, useEffect } from "react";
import axios from "axios";

function AltcoinSeason() {
  const [bitcoinDominance, setBitcoinDominance] = useState(null); // State pentru dominanța Bitcoin
  const [isAltcoinSeason, setIsAltcoinSeason] = useState(false); // State pentru altcoin season
  const [loading, setLoading] = useState(true); // State pentru a gestiona încărcarea

  useEffect(() => {
    // Funcția pentru a obține dominanța Bitcoin
    const fetchDominance = async () => {
      try {
        const response = await axios.get(
          "https://api.coingecko.com/api/v3/global"
        );
        console.log("API Response:", response.data); // Verificăm răspunsul API

        const dominance = response.data.data.market_cap_percentage.bitcoin; // Obținem dominanța Bitcoin
        console.log("Bitcoin Dominance:", dominance); // Verificăm valoarea obținută

        setBitcoinDominance(dominance); // Setăm valoarea în state
        if (dominance < 45) {
          setIsAltcoinSeason(true); // Dacă dominanța Bitcoin este sub 45%, este altcoin season
        } else {
          setIsAltcoinSeason(false); // Altcoin season nu e activ
        }

        setLoading(false); // Setăm loading la false după ce datele sunt obținute
      } catch (error) {
        console.error("Error fetching data:", error); // Tratarea erorii
        setLoading(false); // Setăm loading la false chiar și în cazul unei erori
      }
    };

    fetchDominance(); // Apelăm funcția pentru a aduce datele
  }, []); // Aceasta va rula doar o dată la montarea componentei

  return (
    <div className="bg-white p-6 rounded-lg shadow-lg">
      <h2 className="text-2xl font-semibold text-gray-800">Altcoin Season</h2>
      {loading ? (
        <p className="text-lg">Loading data...</p> // Afișăm loading până când datele sunt disponibile
      ) : bitcoinDominance !== null ? (
        <div className="mt-4">
          <p className="text-lg">
            Bitcoin Dominance: {bitcoinDominance.toFixed(2)}%
          </p>
          <p
            className={`mt-2 text-xl font-semibold ${
              isAltcoinSeason ? "text-green-500" : "text-red-500"
            }`}
          >
            {isAltcoinSeason ? "It's Altcoin Season!" : "It's Bitcoin Season."}
          </p>
        </div>
      ) : (
        <p className="text-lg text-red-500">Error loading data.</p> // Mesaj de eroare dacă datele nu sunt valabile
      )}
    </div>
  );
}

export default AltcoinSeason;
