// src/components/SentimentChart.js
import React, { useEffect, useState } from "react";
import { Line } from "react-chartjs-2";
import axios from "axios";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

// Înregistrăm componentele Chart.js
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

function SentimentChart() {
  const [sentimentData, setSentimentData] = useState({
    labels: ["1h", "4h", "12h", "24h", "7d"],
    datasets: [
      {
        label: "Sentiment Trend",
        data: [0, 0, 0, 0, 0], // Inițial sunt 0 pentru că așteptăm datele de la API
        fill: false,
        borderColor: "rgb(75, 192, 192)",
        tension: 0.1,
      },
    ],
  });

  const fetchSentimentData = async () => {
    try {
      const response = await axios.get(
        "https://api.alternative.me/fng/?limit=5&format=json" // URL-ul pentru Fear and Greed Index
      );

      // Extragem datele de sentiment pentru Bitcoin
      const sentimentScores = response.data.data.map((item) => item.value); // Obținem valorile de la API
      const sentimentLabels = response.data.data.map((item) => item.timestamp); // Etichetele pentru intervale

      setSentimentData({
        labels: sentimentLabels,
        datasets: [
          {
            label: "Sentiment Trend (Fear & Greed Index)",
            data: sentimentScores,
            fill: false,
            borderColor: "rgb(75, 192, 192)",
            tension: 0.1,
          },
        ],
      });
    } catch (error) {
      console.error("Error fetching sentiment data:", error);
    }
  };

  useEffect(() => {
    fetchSentimentData();
  }, []);

  return (
    <section className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-16">
      <h2 className="text-3xl font-semibold text-gray-800">
        Market Sentiment (Fear and Greed Index)
      </h2>
      <div className="mt-6">
        <Line data={sentimentData} />
      </div>
    </section>
  );
}

export default SentimentChart;
