import React, { useEffect, useState } from "react";

const AltcoinSeasonIndex = ({ percentage, isAltcoinSeason }) => {
  const [historicalData, setHistoricalData] = useState({});
  const [yearlyData, setYearlyData] = useState({});

  useEffect(() => {
    // FuncÈ›ie pentru a obÈ›ine datele istorice
    const fetchHistoricalData = async () => {
      try {
        const response = await fetch(
          "https://api.coingecko.com/api/v3/coins/bitcoin/market_chart?vs_currency=usd&days=365"
        );
        const data = await response.json();

        const prices = data.prices;
        const yesterdayPrice = prices[prices.length - 2][1];
        const lastWeekPrice = prices[prices.length - 8][1];
        const lastMonthPrice = prices[prices.length - 30][1];
        const yearlyHigh = Math.max(...prices.map((price) => price[1]));
        const yearlyLow = Math.min(...prices.map((price) => price[1]));

        setHistoricalData({
          yesterday: yesterdayPrice,
          lastWeek: lastWeekPrice,
          lastMonth: lastMonthPrice,
        });

        setYearlyData({
          high: yearlyHigh,
          low: yearlyLow,
        });
      } catch (error) {
        console.error("Error fetching historical data:", error);
      }
    };
    fetchHistoricalData();
  }, []);

  // Culoare pentru bara de progres
  const barColor =
    percentage >= 80
      ? "bg-green-600"
      : percentage >= 60
      ? "bg-green-400"
      : percentage >= 40
      ? "bg-yellow-400"
      : percentage >= 20
      ? "bg-orange-400"
      : "bg-red-500";

  return (
    <div className="p-6 max-w-xs mx-auto bg-white shadow-lg rounded-lg dark:bg-gray-800 transition duration-300">
      <h2 className="text-xl font-bold text-gray-800 dark:text-white mb-4 text-center">
        Altcoin Season Index
      </h2>

      {/* Bara de progres */}
      <div className="relative w-full bg-gray-300 h-4 rounded-full mb-4 overflow-hidden">
        <div
          className={`h-4 rounded-full transition-all duration-500 ${barColor}`}
          style={{ width: `${percentage}%` }}
        ></div>
      </div>

      <div className="mb-4 text-center">
        <p className="font-semibold text-gray-900 dark:text-white">
          {isAltcoinSeason
            ? "It's Altcoin Season! ğŸš€"
            : "It's not Altcoin Season. ğŸ“‰"}
        </p>
        <p className="text-sm text-gray-600 dark:text-gray-300">
          Value: {percentage}%
        </p>
      </div>

      {/* Date istorice */}
      <div className="mb-4">
        <h3 className="text-lg font-semibold text-gray-800 dark:text-white">
          Historical Data:
        </h3>
        <ul className="text-gray-600 dark:text-gray-300">
          <li>
            <strong>Yesterday:</strong> ${historicalData.yesterday}
          </li>
          <li>
            <strong>Last Week:</strong> ${historicalData.lastWeek}
          </li>
          <li>
            <strong>Last Month:</strong> ${historicalData.lastMonth}
          </li>
        </ul>
      </div>

      {/* Yearly High È™i Low */}
      <div>
        <h3 className="text-lg font-semibold text-gray-800 dark:text-white">
          Yearly High/Low:
        </h3>
        <ul className="text-gray-600 dark:text-gray-300">
          <li>
            <strong>Yearly High:</strong> ${yearlyData.high}
          </li>
          <li>
            <strong>Yearly Low:</strong> ${yearlyData.low}
          </li>
        </ul>
      </div>
    </div>
  );
};

export default AltcoinSeasonIndex;
