import React, { useState, useEffect } from "react";
import axios from "axios";

function AltcoinSeason() {
  const [bitcoinDominance, setBitcoinDominance] = useState(null);
  const [isAltcoinSeason, setIsAltcoinSeason] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchDominance = async () => {
      try {
        const response = await axios.get(
          "https://min-api.cryptocompare.com/data/global"
        );
        const dominance = response.data.Data.Bitcoin.Dominance;

        setBitcoinDominance(dominance);
        if (dominance < 45) {
          setIsAltcoinSeason(true);
        } else {
          setIsAltcoinSeason(false);
        }

        setLoading(false);
      } catch (error) {
        console.error("Error fetching data:", error);
        setLoading(false);
      }
    };

    fetchDominance();
  }, []);

  return (
    <div className="bg-white p-6 rounded-lg shadow-lg">
      <h2 className="text-2xl font-semibold text-gray-800">Altcoin Season</h2>
      {loading ? (
        <p className="text-lg">Loading data...</p>
      ) : bitcoinDominance !== null ? (
        <div className="mt-4">
          <p className="text-lg">
            Bitcoin Dominance: {bitcoinDominance.toFixed(2)}%
          </p>
          <p
            className={`mt-2 text-xl font-semibold ${
              isAltcoinSeason ? "text-green-500" : "text-red-500"
            }`}
          >
            {isAltcoinSeason ? "It's Altcoin Season!" : "It's Bitcoin Season."}
          </p>
        </div>
      ) : (
        <p className="text-lg text-red-500">Error loading data.</p>
      )}
    </div>
  );
}

export default AltcoinSeason;
