import React, { useState, useEffect, useMemo } from "react";
import axios from "axios";

const WhaleTransactions = () => {
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [period, setPeriod] = useState(7); // 7 zile
  const [selectedBlockchain, setSelectedBlockchain] = useState("ethereum");

  // Folosim useMemo pentru a memora adresele de portofele
  const walletAddresses = useMemo(
    () => [
      "0x5f7b6fa1c33f77b4e8f7781c7c95160270d3e4f4", // Exemplu de adresă mare
      "0x3c44cdddb6a900fa2b585dd299e03d12fa4293bc", // Alt portofel
      "0x31e3b163fb0f681064bce4c9b0879c9747f34b3c", // Alt portofel
      "0x7a1115b3d859b6f9e2c5b960a41f2e8c4f039767", // Alt portofel
      "0xFf4e0635976B3cb2d4b5D5A5F5A702D0c51C3f7b", // Alt portofel
    ],
    []
  ); // Acesta va fi memorat și nu se va schimba la fiecare renderizare

  useEffect(() => {
    // Funcția pentru a obține tranzacțiile mari
    const fetchTransactions = async () => {
      try {
        const apiKey = "RP1AAGBP2YNUWFTAFP6KWT7GRRKC5BG5MM"; // Înlocuiește cu cheia ta de API de la Etherscan
        const endpoint = `https://api.etherscan.io/api`;

        // Vom colecta tranzacțiile pentru fiecare adresă
        let allTransactions = [];

        for (const address of walletAddresses) {
          const response = await axios.get(endpoint, {
            params: {
              module: "account",
              action: "txlist",
              address: address,
              startblock: 0,
              endblock: 99999999,
              sort: "desc",
              apikey: apiKey,
            },
          });

          // Log pentru a inspecta răspunsul complet
          console.log(`API Response for ${address}:`, response.data);

          // Verificăm dacă răspunsul conține tranzacții și dacă 'result' este un array
          if (response.data.result && Array.isArray(response.data.result)) {
            const largeTransactions = response.data.result.filter(
              (transaction) => {
                const amountInEth =
                  parseFloat(transaction.value) / Math.pow(10, 18); // Convertim valoarea în ETH
                return amountInEth > 1; // Tranzacții mai mari de 1 ETH (pentru testare)
              }
            );

            allTransactions = [...allTransactions, ...largeTransactions];
          } else {
            // Log pentru cazul în care 'result' nu este un array
            console.log(
              `No valid transactions found or invalid format for ${address}`
            );
          }
        }

        setTransactions(allTransactions);
      } catch (error) {
        console.error("Eroare la obținerea tranzacțiilor:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchTransactions();
  }, [period, selectedBlockchain, walletAddresses]); // Refacerea cererii când se schimbă perioada sau blockchain-ul

  if (loading) {
    return <div>Loading transactions...</div>;
  }

  return (
    <div>
      <h2>Whale Transactions (Over 1 ETH)</h2>

      {/* Selectare blockchain */}
      <div>
        <label htmlFor="blockchain">Select Blockchain:</label>
        <select
          id="blockchain"
          value={selectedBlockchain}
          onChange={(e) => setSelectedBlockchain(e.target.value)}
        >
          <option value="ethereum">Ethereum</option>
          <option value="binancecoin">Binance Coin (BSC)</option>
          <option value="bitcoin">Bitcoin</option>
          {/* Adaugă opțiuni suplimentare pentru alte blockchain-uri */}
        </select>
      </div>

      {/* Selectare perioadă */}
      <div>
        <label htmlFor="period">Select Period (Days):</label>
        <input
          id="period"
          type="number"
          value={period}
          onChange={(e) => setPeriod(e.target.value)}
          min="1"
        />
      </div>

      <table>
        <thead>
          <tr>
            <th>Timestamp</th>
            <th>Amount (ETH)</th>
            <th>Blockchain</th>
          </tr>
        </thead>
        <tbody>
          {transactions.length === 0 ? (
            <tr>
              <td colSpan="3">No large transactions found.</td>
            </tr>
          ) : (
            transactions.map((transaction, index) => (
              <tr key={index}>
                <td>
                  {new Date(transaction.timeStamp * 1000).toLocaleString()}
                </td>
                <td>{parseFloat(transaction.value) / Math.pow(10, 18)} ETH</td>
                <td>{selectedBlockchain}</td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
};

export default WhaleTransactions;
