// src/components/NewsCard.js
import React, { useEffect, useState } from "react";
import axios from "axios";

function NewsCard() {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState("");
  const [sortBy, setSortBy] = useState("publishedAt");

  const getNews = async () => {
    try {
      const response = await axios.get(
        `https://newsapi.org/v2/everything?q=crypto&sortBy=${sortBy}&apiKey=b4aeb5d2f71d497a8140ed67aed1037c`
      );
      setNews(response.data.articles || []);
    } catch (error) {
      console.error("Error fetching news:", error);
      setNews([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    getNews();
  }, [sortBy]);

  const filteredNews = news.filter((article) =>
    article.title.toLowerCase().includes(searchQuery.toLowerCase())
  );

  if (loading)
    return (
      <div className="flex justify-center items-center min-h-screen bg-gradient-to-r from-blue-500 to-purple-500">
        <div className="text-white text-2xl animate-pulse">Loading news...</div>
      </div>
    );

  return (
    <div className="p-6">
      <div className="flex flex-col sm:flex-row justify-between mb-6">
        <input
          type="text"
          placeholder="Search news..."
          className="p-2 border border-gray-300 rounded-lg shadow-md w-full sm:w-1/2 mb-4 sm:mb-0"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
        <select
          className="p-2 border border-gray-300 rounded-lg shadow-md"
          value={sortBy}
          onChange={(e) => setSortBy(e.target.value)}
        >
          <option value="publishedAt">Latest</option>
          <option value="relevancy">Most Relevant</option>
          <option value="popularity">Most Popular</option>
        </select>
      </div>

      {filteredNews.length === 0 ? (
        <p className="text-center text-xl text-gray-600">No news found.</p>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
          {filteredNews.map((article, index) => (
            <div
              key={index}
              className="bg-white p-6 rounded-lg shadow-lg hover:shadow-2xl transition-all transform hover:scale-105"
            >
              {article.urlToImage && (
                <img
                  src={article.urlToImage}
                  alt={article.title}
                  className="w-full h-40 object-cover rounded-md mb-4"
                />
              )}
              <h2 className="text-xl font-semibold text-gray-800">
                {article.title}
              </h2>
              <p className="mt-2 text-gray-600">{article.description}</p>
              <a
                href={article.url}
                target="_blank"
                rel="noopener noreferrer"
                className="mt-4 inline-block text-blue-600 hover:text-blue-800 transition"
              >
                Read more
              </a>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default NewsCard;
