// src/components/NewsCard.js
import React, { useEffect, useState } from "react";
import axios from "axios";

function NewsCard() {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(true);

  const getNews = async () => {
    try {
      const response = await axios.get(
        `https://newsapi.org/v2/everything?q=crypto&apiKey=b4aeb5d2f71d497a8140ed67aed1037c`
      );
      if (response.data.articles) {
        setNews(response.data.articles);
      } else {
        setNews([]);
      }
      setLoading(false);
    } catch (error) {
      console.error("Error fetching news:", error);
      setNews([]);
      setLoading(false);
    }
  };

  useEffect(() => {
    getNews();
  }, []);

  if (loading)
    return (
      <div className="flex justify-center items-center min-h-screen bg-gradient-to-r from-blue-500 to-purple-500">
        <div className="text-white text-2xl animate-pulse">Loading news...</div>
      </div>
    );

  if (news.length === 0)
    return <p className="text-center text-xl text-gray-600">No news found.</p>;

  return (
    <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8 p-6">
      {news.map((article, index) => (
        <div
          key={index}
          className="bg-white rounded-lg shadow-lg overflow-hidden hover:shadow-2xl transition-transform transform hover:scale-105"
        >
          {article.urlToImage && (
            <img
              src={article.urlToImage}
              alt={article.title}
              className="w-full h-48 object-cover"
            />
          )}
          <div className="p-5">
            <h2 className="text-xl font-semibold text-gray-800">
              {article.title}
            </h2>
            <p className="mt-3 text-gray-600 text-sm">{article.description}</p>
            <a
              href={article.url}
              target="_blank"
              rel="noopener noreferrer"
              className="mt-4 inline-block text-blue-600 hover:text-blue-800 font-medium transition duration-200"
            >
              Read more â†’
            </a>
          </div>
        </div>
      ))}
    </div>
  );
}

export default NewsCard;
