// src/components/SentimentGauge.js
import React from "react";
import { CircularProgressbar, buildStyles } from "react-circular-progressbar";
import "react-circular-progressbar/dist/styles.css";

const SentimentGauge = ({ value }) => {
  // Culorile de la roșu (frică) la verde (lăcomie)
  const getColor = (score) => {
    if (score <= 25) return "#ff4d4d"; // Frică extremă - roșu
    if (score <= 50) return "#ffa500"; // Frică - portocaliu
    if (score <= 75) return "#f0e68c"; // Neutru - galben
    return "#23d996"; // Lăcomie - verde
  };

  return (
    <div className="flex flex-col items-center mt-6">
      <div className="w-48 h-48">
        {" "}
        {/* Dimensiune mărită */}
        <CircularProgressbar
          value={value}
          text={`${value}`}
          styles={buildStyles({
            textColor: "#fff",
            pathColor: getColor(value),
            trailColor: "#2d2d2d",
            textSize: "24px",
            strokeLinecap: "round",
          })}
        />
      </div>
      <p className="mt-2 text-lg font-semibold text-gray-800 dark:text-white">
        Fear & Greed Index
      </p>
    </div>
  );
};

export default SentimentGauge;
