// src/components/DayNightToggle.js
import React, { useState, useEffect } from "react";
import { FaSun, FaMoon } from "react-icons/fa"; // Importă iconițele

const DayNightToggle = ({ isDarkMode, setIsDarkMode }) => {
  const [darkMode, setDarkMode] = useState(isDarkMode);

  useEffect(() => {
    const savedMode = localStorage.getItem("darkMode");
    if (savedMode) {
      setDarkMode(savedMode === "true");
    }
  }, []);

  useEffect(() => {
    localStorage.setItem("darkMode", darkMode);
    if (darkMode) {
      document.documentElement.classList.add("dark");
    } else {
      document.documentElement.classList.remove("dark");
    }
  }, [darkMode]);

  const handleToggle = () => {
    setDarkMode(!darkMode);
  };

  return (
    <button
      onClick={handleToggle}
      className="p-2 rounded-md bg-gray-200 dark:bg-gray-700 text-gray-900 dark:text-white"
    >
      {darkMode ? <FaMoon size={24} /> : <FaSun size={24} />}
    </button>
  );
};

export default DayNightToggle;
