import React, { useEffect, useState } from "react";

const AltcoinSeason = () => {
  const [isAltcoinSeason, setIsAltcoinSeason] = useState(null);
  const [loading, setLoading] = useState(true);
  const [outperformingCount, setOutperformingCount] = useState(0);
  const [totalAltcoins, setTotalAltcoins] = useState(0);
  const [percentage, setPercentage] = useState(0); // Procentajul actualizat

  useEffect(() => {
    const fetchAltcoinSeasonData = async () => {
      try {
        setLoading(true);

        // Fetch primele 50 de criptomonede (exclude stablecoins și asset-backed tokens)
        const response = await fetch(
          "https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=50&page=1"
        );
        const data = await response.json();

        console.log("API Data:", data); // Verificăm datele returnate

        // Lista de monede pe care le excludem (stablecoins și asset-backed tokens)
        const excludedCoins = [
          "tether",
          "usd-coin",
          "wrapped-bitcoin",
          "staked-ether",
          "binance-usd",
          "dai",
          "trueusd",
        ];

        // Filtrăm monedele pentru a exclude stablecoins și asset-backed tokens
        const filteredData = data.filter(
          (coin) => !excludedCoins.includes(coin.id)
        );

        console.log("Filtered Data:", filteredData); // Verificăm datele filtrate

        let outperformingCountTemp = 0;
        const totalAltcoinsTemp = filteredData.length;

        // Verificăm performanța fiecărei monede față de Bitcoin pe ultimele 24 de ore
        const bitcoin = data.find((coin) => coin.id === "bitcoin");
        if (!bitcoin) {
          console.error("Bitcoin data not found");
          return;
        }

        // Verificăm dacă Bitcoin și monedele au date pentru schimbarea pe 24 de ore
        for (let coin of filteredData) {
          if (
            coin.price_change_percentage_24h &&
            bitcoin.price_change_percentage_24h
          ) {
            console.log(
              `${coin.name}: ${coin.price_change_percentage_24h}% | Bitcoin: ${bitcoin.price_change_percentage_24h}%`
            ); // Log pentru comparare
            if (
              coin.price_change_percentage_24h >
              bitcoin.price_change_percentage_24h
            ) {
              outperformingCountTemp++;
            }
          }
        }

        setOutperformingCount(outperformingCountTemp);
        setTotalAltcoins(totalAltcoinsTemp);

        // Calculăm procentajul de altcoins care au depășit Bitcoin
        const percentageTemp =
          (outperformingCountTemp / totalAltcoinsTemp) * 100;
        setPercentage(percentageTemp); // Actualizăm procentajul

        // Dacă 75% din criptomonedele performează mai bine decât Bitcoin, considerăm că suntem în Altcoin Season
        setIsAltcoinSeason(percentageTemp >= 75);
      } catch (error) {
        console.error("Error fetching altcoin season data:", error);
        setIsAltcoinSeason(false);
      } finally {
        setLoading(false);
      }
    };

    fetchAltcoinSeasonData();
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  // Determinăm culoarea barei în funcție de procentaj
  let barColor = "#ff4444"; // Default - roșu (nu suntem în Altcoin Season)
  if (percentage >= 75) {
    barColor = "#23d996"; // Verde - suntem în Altcoin Season
  } else if (percentage > 0) {
    barColor = "#fcd34d"; // Galben - parțial performanță
  }

  return (
    <div className="p-4 rounded-lg bg-gray-200 text-center">
      <h2 className="text-xl font-semibold mb-2">
        {isAltcoinSeason
          ? "It's Altcoin Season (75% of the Top 50 coins are outperforming Bitcoin)"
          : "It's not Altcoin Season."}
      </h2>

      <div className="w-full bg-gray-300 h-4 rounded-full">
        <div
          className="h-4 rounded-full"
          style={{
            width: `${percentage}%`, // Folosim procentajul calculat
            backgroundColor: barColor,
          }}
        ></div>
      </div>

      <p className="mt-2 text-lg">
        {outperformingCount} of {totalAltcoins} altcoins have performed better
        than Bitcoin in the last 24 hours.
      </p>
    </div>
  );
};

export default AltcoinSeason;
