// src/App.js
import React, { useState } from "react";
import Header from "./components/Header";
import SentimentChart from "./components/SentimentChart";
import NewsCard from "./components/NewsCard";
import AltcoinSeason from "./components/AltcoinSeason";
import Footer from "./components/Footer";

function App() {
  const [isNightMode, setIsNightMode] = useState(false); // Trebuie să o ai aici

  // Funcția pentru a schimba Night Mode
  const toggleNightMode = (newMode) => {
    setIsNightMode(newMode);
  };

  return (
    <div
      className={`App ${
        isNightMode ? "bg-gray-900 text-white" : "bg-gray-100 text-black"
      } min-h-screen`}
    >
      <Header onToggleNightMode={toggleNightMode} />{" "}
      {/* Transmitem funcția în Header */}
      <SentimentChart />
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
        <AltcoinSeason />
        <NewsCard />
      </main>
      <Footer />
    </div>
  );
}

export default App;
