import React, { useEffect, useState } from "react";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
} from "recharts";

const AltcoinChart = ({ coin, onClose }) => {
  const [chartData, setChartData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchHistoricalData = async () => {
      try {
        const response = await fetch(
          `https://api.coingecko.com/api/v3/coins/${coin.id}/market_chart?vs_currency=usd&days=7`
        );
        const data = await response.json();

        const formattedData = data.prices.map((priceData) => ({
          date: new Date(priceData[0]).toLocaleDateString("en-GB", {
            day: "2-digit",
            month: "2-digit",
          }),
          price: priceData[1].toFixed(2),
        }));

        setChartData(formattedData);
      } catch (error) {
        console.error("Error fetching historical data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchHistoricalData();
  }, [coin.id]);

  if (loading) {
    return (
      <div className="w-96 p-4 bg-white dark:bg-gray-800 shadow-lg rounded-lg">
        <p className="text-gray-700 dark:text-gray-300">Loading chart...</p>
      </div>
    );
  }

  const prices = chartData.map((data) => parseFloat(data.price));
  const minPrice = Math.min(...prices);
  const maxPrice = Math.max(...prices);
  const priceRange = maxPrice - minPrice;
  const margin = priceRange * 0.05;
  const yDomain = [Math.max(0, minPrice - margin), maxPrice + margin];

  return (
    <div className="w-96 p-4 bg-white dark:bg-gray-900 shadow-lg rounded-lg max-h-[500px] overflow-y-auto">
      <button onClick={onClose} className="text-gray-700 dark:text-white mb-4">
        Close
      </button>
      <h2 className="text-xl font-bold text-gray-900 dark:text-white mb-2">
        {coin.name}
      </h2>
      <p className="text-gray-700 dark:text-gray-300 mb-4">
        Price Change: {coin.priceChange > 0 ? "+" : ""}
        {coin.priceChange.toFixed(2)}%
      </p>
      <img
        src={coin.image}
        alt={coin.name}
        className="w-12 h-12 rounded-full mb-4"
      />

      <div className="w-full h-48">
        <ResponsiveContainer width="100%" height="100%">
          <LineChart data={chartData}>
            <CartesianGrid
              strokeDasharray="none"
              stroke="#eee"
              vertical={false}
              horizontal={false}
            />
            <XAxis
              dataKey="date"
              tick={{
                fill: "#666",
                fontSize: 10,
                angle: 0,
                textAnchor: "middle",
              }}
              interval={Math.floor(chartData.length / 7)}
              axisLine={{ stroke: "#666" }}
            />
            <YAxis
              tickFormatter={(value) => `$${parseFloat(value).toFixed(2)}`}
              tick={{ fill: "#666", fontSize: 10 }}
              axisLine={{ stroke: "#666" }}
              domain={yDomain}
            />
            <Tooltip
              contentStyle={{
                backgroundColor: "#fff",
                border: "1px solid #ddd",
                borderRadius: "4px",
              }}
            />
            <Line
              type="monotone"
              dataKey="price"
              stroke="#23d996"
              strokeWidth={2}
              dot={false}
            />
          </LineChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
};

export default AltcoinChart;
