// src/components/NightToggle.js
import React, { useState, useEffect } from "react";
import { FaSun, FaMoon } from "react-icons/fa";

function NightToggle() {
  const [isNightMode, setIsNightMode] = useState(false);

  // Verificăm tema salvată în localStorage la încărcarea componentei
  useEffect(() => {
    const savedTheme = localStorage.getItem("theme");
    if (savedTheme === "dark") {
      setIsNightMode(true);
      document.body.classList.add("dark");
    } else {
      setIsNightMode(false);
      document.body.classList.remove("dark");
    }
  }, []);

  // Funcția de toggle pentru schimbarea temei
  const toggleTheme = () => {
    setIsNightMode(!isNightMode);
    if (isNightMode) {
      document.body.classList.remove("dark");
      localStorage.setItem("theme", "light");
    } else {
      document.body.classList.add("dark");
      localStorage.setItem("theme", "dark");
    }
  };

  return (
    <button
      onClick={toggleTheme}
      className="text-white p-2 rounded-full hover:bg-gray-700 focus:outline-none"
    >
      {isNightMode ? (
        <FaMoon className="text-xl" />
      ) : (
        <FaSun className="text-xl" />
      )}
    </button>
  );
}

export default NightToggle;
