// src/components/DayNightToggle.js
import React, { useEffect } from "react";
import { FaSun, FaMoon } from "react-icons/fa"; // Importă iconițele

const DayNightToggle = ({ isDarkMode, setIsDarkMode }) => {
  // Setează tema din localStorage atunci când componenta este încărcată
  useEffect(() => {
    const savedMode = localStorage.getItem("darkMode");
    if (savedMode) {
      setIsDarkMode(savedMode === "true");
    }
  }, [setIsDarkMode]);

  // Salvează tema în localStorage și aplică stilurile corespunzătoare
  useEffect(() => {
    localStorage.setItem("darkMode", isDarkMode);
    if (isDarkMode) {
      document.documentElement.classList.add("dark");
    } else {
      document.documentElement.classList.remove("dark");
    }
  }, [isDarkMode]);

  // Schimbă starea temei
  const toggleDarkMode = () => {
    setIsDarkMode(!isDarkMode);
  };

  return (
    <button
      onClick={toggleDarkMode}
      className="p-2 rounded-md bg-gray-200 dark:bg-gray-700 text-gray-900 dark:text-white"
    >
      {isDarkMode ? <FaMoon size={24} /> : <FaSun size={24} />}
    </button>
  );
};

export default DayNightToggle;
