import React, { useState, useEffect } from "react";
import NightToggle from "./NightToggle"; // Importă componenta NightToggle

function Header({ setActiveTab }) {
  const [cryptoData, setCryptoData] = useState({
    bitcoinDominance: 0,
    ethereumDominance: 0,
    totalVolume24h: 0,
    totalMarketCap: 0,
    marketCapChange24h: 0,
  });

  useEffect(() => {
    // Funcție pentru a obține datele de la CoinGecko
    const fetchCoinGeckoData = async () => {
      try {
        const response = await fetch("https://api.coingecko.com/api/v3/global");
        const data = await response.json();
        console.log("API Response:", data); // Debugging: Afișează răspunsul API

        // Extrage datele necesare
        const globalMetrics = data.data;
        const bitcoinDominance = globalMetrics.market_cap_percentage.btc;
        const ethereumDominance = globalMetrics.market_cap_percentage.eth;
        const totalVolume24h = globalMetrics.total_volume.usd;
        const totalMarketCap = globalMetrics.total_market_cap.usd;
        const marketCapChange24h =
          globalMetrics.market_cap_change_percentage_24h_usd;

        // Actualizează state-ul cu noile date
        setCryptoData({
          bitcoinDominance,
          ethereumDominance,
          totalVolume24h,
          totalMarketCap,
          marketCapChange24h,
        });
      } catch (error) {
        console.error("Error fetching CoinGecko data:", error); // Debugging: Afișează erorile
      }
    };

    fetchCoinGeckoData();
  }, []);

  return (
    <header className="bg-gradient-to-r from-gray-800 to-gray-900 text-white py-6 shadow-xl">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Titlul site-ului, navbar-ul și NightToggle pe aceeași linie */}
        <div className="flex items-center justify-between">
          {/* Partea din stânga: Titlul și navbar-ul */}
          <div className="flex items-center">
            {/* Titlul site-ului */}
            <h1 className="text-3xl font-extrabold tracking-tight text-transparent bg-clip-text bg-gradient-to-r from-teal-400 to-green-500 hover:from-green-400 hover:to-teal-500 transition-all duration-500">
              SentimentX
            </h1>

            {/* Navbar */}
            <nav className="flex space-x-6 text-lg font-semibold ml-10">
              <button
                className="text-white hover:text-teal-300 transition duration-300 transform hover:scale-105"
                onClick={() => setActiveTab("news")}
              >
                News
              </button>

              <button
                className="text-white hover:text-teal-300 transition duration-300 transform hover:scale-105"
                onClick={() => setActiveTab("altcoin")}
              >
                Altcoin Season Index
              </button>

              <button
                className="text-white hover:text-teal-300 transition duration-300 transform hover:scale-105"
                onClick={() => setActiveTab("sentiment")}
              >
                Sentiment Trend
              </button>

              <button
                className="text-white hover:text-teal-300 transition duration-300 transform hover:scale-105"
                onClick={() => setActiveTab("whaleTransactions")}
              >
                Whale Transactions
              </button>
            </nav>
          </div>

          {/* Partea din dreapta: NightToggle */}
          <div className="flex items-center">
            <NightToggle />
          </div>
        </div>

        {/* Linie orizontală */}
        <hr className="border-gray-700 my-4" />

        {/* Informații despre crypto */}
        <div className="grid grid-cols-2 sm:grid-cols-4 gap-4 text-sm font-medium text-gray-300">
          {/* Bitcoin Dominance */}
          <div className="flex items-center space-x-2 bg-gray-700/50 p-3 rounded-lg hover:bg-gray-700/70 transition duration-300">
            <span className="text-teal-400">📈</span>
            <span>
              BTC Dominance:{" "}
              <span className="font-bold">
                {cryptoData.bitcoinDominance.toFixed(2)}%
              </span>
            </span>
          </div>

          {/* Ethereum Dominance */}
          <div className="flex items-center space-x-2 bg-gray-700/50 p-3 rounded-lg hover:bg-gray-700/70 transition duration-300">
            <span className="text-purple-400">📊</span>
            <span>
              ETH Dominance:{" "}
              <span className="font-bold">
                {cryptoData.ethereumDominance.toFixed(2)}%
              </span>
            </span>
          </div>

          {/* 24h Volume */}
          <div className="flex items-center space-x-2 bg-gray-700/50 p-3 rounded-lg hover:bg-gray-700/70 transition duration-300">
            <span className="text-yellow-400">💹</span>
            <span>
              24h Vol:{" "}
              <span className="font-bold">
                ${cryptoData.totalVolume24h.toLocaleString()}
              </span>
            </span>
          </div>

          {/* Market Cap */}
          <div className="flex items-center space-x-2 bg-gray-700/50 p-3 rounded-lg hover:bg-gray-700/70 transition duration-300">
            <span className="text-blue-400">🌐</span>
            <span>
              Market Cap:{" "}
              <span className="font-bold">
                ${cryptoData.totalMarketCap.toLocaleString()}
              </span>{" "}
              <span
                className={
                  cryptoData.marketCapChange24h >= 0
                    ? "text-green-400"
                    : "text-red-400"
                }
              >
                {cryptoData.marketCapChange24h >= 0 ? "▲" : "▼"}{" "}
                {Math.abs(cryptoData.marketCapChange24h).toFixed(2)}%
              </span>
            </span>
          </div>
        </div>
      </div>
    </header>
  );
}

export default Header;
