import { useState, useEffect } from "react";
import { FaEthereum, FaBitcoin } from "react-icons/fa";
import { formatDistanceToNow } from "date-fns";

const WhaleTransactions = () => {
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchWhaleTransactions = async () => {
      const API_KEY_BSC = "P51M4PQJ9KBGFGNXJFUETUJ2HSNJ98Y26T"; // Înlocuiește cu cheia ta BscScan

      const wallets = [
        {
          name: "Binance BSC",
          address: "0x8894E0a0c962CB723c1976a4421c95949bE2D4E3", // Adresă BSC de exemplu
          blockchain: "BSC",
          apiKey: API_KEY_BSC,
        },
      ];

      const allTransactions = [];

      for (let wallet of wallets) {
        const { address, blockchain, apiKey, name } = wallet;
        const url = `https://api.bscscan.com/api?module=account&action=txlist&address=${address}&startblock=0&endblock=99999999&page=1&offset=10&sort=desc&apikey=${apiKey}`;

        try {
          const response = await fetch(url);
          const data = await response.json();
          console.log("API Response:", data); // Verifică răspunsul API

          if (data.status === "1") {
            const filteredTransactions = data.result.filter(
              (tx) => parseFloat(tx.value) > 10 ** 18 * 1 // Filtrează tranzacții mai mari de 1 BNB
            );
            console.log("Filtered Transactions:", filteredTransactions); // Verifică tranzacțiile filtrate

            const transactionsWithData = filteredTransactions.map((tx) => {
              console.log("Processing Transaction:", tx); // Verifică fiecare tranzacție
              return {
                hash: tx.hash,
                from: tx.from,
                to: tx.to,
                value: tx.value / 10 ** 18, // Valoarea în BNB
                blockchain: "BSC",
                date: new Date(tx.timeStamp * 1000).toLocaleString(),
                fee: tx.gasUsed ? (tx.gasUsed * tx.gasPrice) / 10 ** 18 : "N/A", // Taxa în BNB
                block: tx.blockNumber,
                age: formatDistanceToNow(new Date(tx.timeStamp * 1000)),
                icon: <FaBitcoin className="text-yellow-400 text-xl" />,
              };
            });

            allTransactions.push(...transactionsWithData);
          } else {
            console.error(`No transactions found for wallet: ${name}`);
          }
        } catch (error) {
          console.error("Error fetching transactions:", error);
        }
      }

      console.log("All Transactions:", allTransactions); // Verifică toate tranzacțiile
      setTransactions(allTransactions.slice(0, 20)); // Limitează la primele 20 tranzacții
      setLoading(false);
    };

    fetchWhaleTransactions();
  }, []);

  const truncate = (str) => {
    return str.length > 15 ? `${str.slice(0, 6)}...${str.slice(-4)}` : str;
  };

  return (
    <div className="container mx-auto p-6">
      <h2 className="text-3xl font-bold text-center mb-8 text-transparent bg-clip-text bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500 dark:text-transparent dark:bg-clip-text dark:bg-gradient-to-r dark:from-teal-300 dark:to-green-400">
        Whale Transactions
      </h2>
      {loading ? (
        <p className="text-center text-xl text-gray-600">
          Loading transactions...
        </p>
      ) : (
        <div className="bg-white rounded-lg shadow-lg p-6">
          {transactions.length > 0 ? (
            <div className="space-y-4">
              {transactions.map((tx, index) => (
                <div key={index} className="p-4 border-b border-gray-300">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-4">
                      <div className="w-12 h-12 bg-indigo-100 rounded-full flex justify-center items-center">
                        {tx.icon}
                      </div>
                      <div>
                        <div className="font-semibold text-lg text-gray-800">
                          {tx.blockchain} Transaction
                        </div>
                        <div className="text-sm text-gray-500">
                          {tx.age} ago
                        </div>
                      </div>
                    </div>
                    <div>
                      <a
                        href={`https://bscscan.com/tx/${tx.hash}`}
                        className="text-cyan-500"
                        target="_blank"
                        rel="noopener noreferrer"
                      >
                        View on BscScan
                      </a>
                    </div>
                  </div>
                  <div className="mt-4 grid grid-cols-3 gap-6">
                    <div>
                      <strong>From:</strong>{" "}
                      <a
                        href={`https://bscscan.com/address/${tx.from}`}
                        className="text-cyan-500"
                        target="_blank"
                        rel="noopener noreferrer"
                      >
                        {truncate(tx.from)}
                      </a>
                    </div>
                    <div>
                      <strong>To:</strong>{" "}
                      <a
                        href={`https://bscscan.com/address/${tx.to}`}
                        className="text-cyan-500"
                        target="_blank"
                        rel="noopener noreferrer"
                      >
                        {truncate(tx.to)}
                      </a>
                    </div>
                    <div>
                      <strong>Amount:</strong> {tx.value} BNB
                    </div>
                    <div>
                      <strong>Block:</strong> {tx.block}
                    </div>
                    <div>
                      <strong>Fee:</strong> {tx.fee} BNB
                    </div>
                    <div>
                      <strong>Transaction Hash:</strong>{" "}
                      <a
                        href={`https://bscscan.com/tx/${tx.hash}`}
                        className="text-cyan-500"
                        target="_blank"
                        rel="noopener noreferrer"
                      >
                        {truncate(tx.hash)}
                      </a>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <p className="text-center text-xl text-gray-600">
              No large transactions available at the moment.
            </p>
          )}
        </div>
      )}
    </div>
  );
};

export default WhaleTransactions;
