import React, { useEffect, useState } from "react";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
} from "recharts";

const AltcoinChart = ({ coin, onClose }) => {
  const [chartData, setChartData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [isExpanded, setIsExpanded] = useState(false);
  const [waiting, setWaiting] = useState(false); // Stare pentru afișarea mesajului de așteptare

  // Funcție pentru delay
  const delay = (ms) => new Promise((resolve) => setTimeout(resolve, ms));

  useEffect(() => {
    const fetchHistoricalData = async () => {
      try {
        setLoading(true);
        setWaiting(true); // Afișează mesajul de așteptare

        // Așteaptă 2 secunde înainte de a face cererea
        await delay(2000);

        // Face cererea către serverul tău
        const response = await fetch(
          `http://localhost:5000/api/market-chart?coinId=${coin.id}&vsCurrency=usd&days=7`
        );

        if (!response.ok) {
          throw new Error(`Server returned status: ${response.status}`);
        }

        const data = await response.json();

        // Verifică dacă datele sunt valide
        if (!data || !data.prices) {
          throw new Error("Invalid data format: prices not found");
        }

        // Formatează datele pentru grafic
        const formattedData = data.prices.map((priceData) => ({
          date: new Date(priceData[0]).toLocaleDateString("en-GB", {
            day: "2-digit",
            month: "2-digit",
          }),
          price: priceData[1].toFixed(2),
        }));

        setChartData(formattedData);
      } catch (error) {
        console.error("Error fetching historical data:", error);
      } finally {
        setLoading(false);
        setWaiting(false); // Ascunde mesajul de așteptare
      }
    };

    fetchHistoricalData();
  }, [coin.id]);

  if (loading) {
    return (
      <div className="w-96 p-4 bg-white dark:bg-gray-800 shadow-lg rounded-lg">
        <p className="text-gray-700 dark:text-gray-300">Loading chart...</p>
      </div>
    );
  }

  // Calculează domeniul pentru axa Y
  const prices = chartData.map((data) => parseFloat(data.price));
  const minPrice = Math.min(...prices);
  const maxPrice = Math.max(...prices);
  const priceRange = maxPrice - minPrice;
  const margin = priceRange * 0.05;
  const yDomain = [Math.max(0, minPrice - margin), maxPrice + margin];

  return (
    <div
      className={`${
        isExpanded ? "w-[600px]" : "w-96"
      } p-4 bg-white dark:bg-gray-900 shadow-lg rounded-lg flex flex-col`}
    >
      {/* Butoane pentru închidere și expandare */}
      <div className="flex justify-between items-center mb-4">
        <button
          onClick={onClose}
          className="text-gray-700 dark:text-white hover:text-gray-900 dark:hover:text-gray-300"
        >
          Close
        </button>
        <button
          onClick={() => setIsExpanded(!isExpanded)}
          className="text-gray-700 dark:text-white hover:text-gray-900 dark:hover:text-gray-300"
        >
          {isExpanded ? "Minimize" : "Expand"}
        </button>
      </div>

      {/* Detalii despre monedă */}
      <h2 className="text-xl font-bold text-gray-900 dark:text-white mb-2">
        {coin.name}
      </h2>
      <p className="text-gray-700 dark:text-gray-300 mb-4">
        Price Change: {coin.priceChange > 0 ? "+" : ""}
        {coin.priceChange.toFixed(2)}%
      </p>
      <img
        src={coin.image}
        alt={coin.name}
        className="w-12 h-12 rounded-full mb-4"
      />

      {/* Mesaj de așteptare */}
      {waiting && (
        <div className="mb-4 text-center text-gray-700 dark:text-gray-300">
          Please wait a moment while we fetch the data...
        </div>
      )}

      {/* Graficul */}
      <div className="flex-1">
        <ResponsiveContainer width="100%" height={isExpanded ? 400 : 200}>
          <LineChart data={chartData}>
            <CartesianGrid
              strokeDasharray="none"
              stroke="#eee"
              vertical={false}
              horizontal={false}
            />
            <XAxis
              dataKey="date"
              tick={{
                fill: "#666",
                fontSize: 10,
                angle: 0,
                textAnchor: "middle",
              }}
              interval={Math.floor(chartData.length / 7)}
              axisLine={{ stroke: "#666" }}
            />
            <YAxis
              tickFormatter={(value) => `$${parseFloat(value).toFixed(2)}`}
              tick={{ fill: "#666", fontSize: 10 }}
              axisLine={{ stroke: "#666" }}
              domain={yDomain}
            />
            <Tooltip
              contentStyle={{
                backgroundColor: "#fff",
                border: "1px solid #ddd",
                borderRadius: "4px",
              }}
            />
            <Line
              type="monotone"
              dataKey="price"
              stroke="#23d996"
              strokeWidth={2}
              dot={false}
            />
          </LineChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
};

export default AltcoinChart;
