// src/App.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import Header from "./components/Header";
import SentimentChart from "./components/SentimentChart"; // Asigură-te că o importi aici
import NewsCard from "./components/NewsCard";
import Footer from "./components/Footer";

function App() {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(true);

  const getNews = async () => {
    try {
      const response = await axios.get(
        `https://newsapi.org/v2/everything?q=crypto&apiKey=b4aeb5d2f71d497a8140ed67aed1037c`
      );
      if (response.data.articles) {
        setNews(response.data.articles);
      } else {
        setNews([]);
      }
      setLoading(false); // Asigură-te că setăm loading la false după ce s-au încărcat datele
    } catch (error) {
      console.error("Error fetching news:", error);
      setNews([]);
      setLoading(false);
    }
  };

  useEffect(() => {
    getNews(); // Apelează funcția pentru a obține știrile
  }, []);

  return (
    <div className="App bg-gray-100 min-h-screen">
      <Header />

      {/* Adăugăm SentimentChart aici, înainte de news */}
      <SentimentChart />

      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
        <NewsCard news={news} loading={loading} setLoading={setLoading} />
      </main>

      <Footer />
    </div>
  );
}

export default App;
