import React, { useState, useEffect } from "react";
import NightToggle from "./NightToggle"; // Importă componenta NightToggle

function Header({ setActiveTab }) {
  const [cryptoData, setCryptoData] = useState({
    bitcoinPrice: 0,
    ethereumPrice: 0,
    bitcoinDominance: 0,
    totalVolume: 0,
  });

  useEffect(() => {
    // Exemplu de apel API folosind CoinGecko
    const fetchCryptoData = async () => {
      try {
        const response = await fetch("https://api.coingecko.com/api/v3/global");
        const data = await response.json();
        const bitcoinData = data.data.market_cap_percentage.btc;
        const ethereumData = data.data.market_cap_percentage.eth;
        const totalVolume = data.data.total_volume.usd;

        setCryptoData({
          bitcoinPrice: data.data.market_cap_percentage.btc,
          ethereumPrice: data.data.market_cap_percentage.eth,
          bitcoinDominance: bitcoinData,
          totalVolume: totalVolume,
        });
      } catch (error) {
        console.error("Error fetching crypto data:", error);
      }
    };

    fetchCryptoData();
  }, []);

  return (
    <header className="bg-gradient-to-r from-gray-800 to-gray-900 text-white py-6 shadow-lg">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Titlul site-ului */}
        <h1 className="text-2xl font-extrabold tracking-tight text-transparent bg-clip-text bg-gradient-to-r from-teal-300 to-green-400 text-left">
          SentimentX
        </h1>

        {/* Linie orizontală */}
        <hr className="border-gray-700 my-4" />

        {/* Informații despre crypto */}
        <div className="flex space-x-8 text-sm font-medium text-gray-300 mt-4">
          <span>BTC: ${cryptoData.bitcoinPrice.toLocaleString()}</span>
          <span>ETH: ${cryptoData.ethereumPrice.toLocaleString()}</span>
          <span>BTC Dominance: {cryptoData.bitcoinDominance.toFixed(2)}%</span>
          <span>24h Vol: ${cryptoData.totalVolume.toLocaleString()}</span>
        </div>

        {/* Navbar și Night Toggle */}
        <div className="flex justify-between items-center mt-6">
          <nav className="flex space-x-8 text-lg font-semibold">
            <button
              className="text-white hover:text-teal-300 transition duration-300"
              onClick={() => setActiveTab("news")}
            >
              News
            </button>

            <button
              className="text-white hover:text-teal-300 transition duration-300"
              onClick={() => setActiveTab("altcoin")}
            >
              Altcoin Season Index
            </button>

            <button
              className="text-white hover:text-teal-300 transition duration-300"
              onClick={() => setActiveTab("sentiment")}
            >
              Sentiment Trend
            </button>

            <button
              className="text-white hover:text-teal-300 transition duration-300"
              onClick={() => setActiveTab("whaleTransactions")}
            >
              Whale Transactions
            </button>
          </nav>

          {/* Night Toggle */}
          <NightToggle />
        </div>
      </div>
    </header>
  );
}

export default Header;
