// src/components/SentimentGauge.js
import React from "react";
import { CircularProgressbar, buildStyles } from "react-circular-progressbar";
import "react-circular-progressbar/dist/styles.css";

const SentimentGauge = ({ value }) => {
  // Determinăm culoarea pe baza valorii indexului
  const getColor = (value) => {
    if (value < 25) return "#FF4444"; // Roșu - Frică extremă
    if (value < 50) return "#FB923C"; // Portocaliu - Frică
    if (value < 75) return "#FCD34D"; // Galben - Neutru
    return "#16A34A"; // Verde - Lăcomie
  };

  return (
    <div className="w-32 h-32 mx-auto">
      <CircularProgressbar
        value={value}
        text={`${value}`}
        styles={buildStyles({
          textColor: "#000",
          pathColor: getColor(value),
          trailColor: "#ddd",
          textSize: "22px",
        })}
      />
    </div>
  );
};

export default SentimentGauge;
