import React, { useState } from "react";
import NightToggle from "./NightToggle"; // Importă componenta NightToggle

function Header() {
  const [activeTab, setActiveTab] = useState("news"); // Setăm tab-ul activ (default este News)

  return (
    <header className="bg-gradient-to-r from-blue-600 to-indigo-700 text-white py-6 shadow-lg">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center">
          {/* Title */}
          <h1 className="text-4xl font-extrabold tracking-tight text-transparent bg-clip-text bg-gradient-to-r from-teal-300 to-green-400 dark:text-white">
            Crypto News and Sentiment Analysis
          </h1>

          {/* Night Toggle */}
          <NightToggle />
        </div>

        {/* Navigation */}
        <nav className="mt-6 flex justify-center space-x-8 text-lg font-semibold">
          <button
            className={`text-white hover:text-teal-300 transition duration-300 ${
              activeTab === "news" ? "border-b-4 border-teal-400" : ""
            }`}
            onClick={() => setActiveTab("news")}
          >
            News
          </button>

          <button
            className={`text-white hover:text-teal-300 transition duration-300 ${
              activeTab === "altcoin" ? "border-b-4 border-teal-400" : ""
            }`}
            onClick={() => setActiveTab("altcoin")}
          >
            AltcoinSeason Index
          </button>

          <button
            className={`text-white hover:text-teal-300 transition duration-300 ${
              activeTab === "sentiment" ? "border-b-4 border-teal-400" : ""
            }`}
            onClick={() => setActiveTab("sentiment")}
          >
            Sentiment Trend
          </button>
        </nav>

        {/* Content display based on active tab */}
        <div className="mt-8">
          {activeTab === "news" && (
            <p className="text-white">Displaying News component...</p>
          )}
          {activeTab === "altcoin" && (
            <p className="text-white">
              Displaying AltcoinSeason Index component...
            </p>
          )}
          {activeTab === "sentiment" && (
            <p className="text-white">
              Displaying Sentiment Trend component...
            </p>
          )}
        </div>
      </div>
    </header>
  );
}

export default Header;
