// src/components/Header.js
import React from "react";
import NightModeToggle from "./NightModeToggle"; // Importă componenta NightModeToggle

function Header({ isNightMode, onToggleNightMode }) {
  return (
    <header
      className={`bg-gradient-to-r from-blue-600 to-indigo-700 py-10 shadow-lg ${
        isNightMode ? "text-white" : "text-black"
      }`}
    >
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center relative">
        <h1 className="text-5xl font-extrabold tracking-tight text-transparent bg-clip-text bg-gradient-to-r from-teal-300 to-green-400">
          Crypto News and Sentiment Analysis
        </h1>

        {/* Plasează butonul de Night Mode în colțul din dreapta sus */}
        <NightModeToggle onToggle={onToggleNightMode} />
      </div>
    </header>
  );
}

export default Header;
