import React, { useEffect, useState } from "react";

const AltcoinSeason = () => {
  const [altcoinsOutperformingBTC, setAltcoinsOutperformingBTC] =
    useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchAltcoinSeason = async () => {
      try {
        setLoading(true);

        // Fetch datele pentru primele 50 de criptomonede, excluzând stablecoins și asset-backed tokens
        const response = await fetch(
          "https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=50&page=1"
        );
        const data = await response.json();

        // Excludem stablecoins și asset-backed tokens
        const excludedCoins = [
          "tether",
          "dai",
          "wrapped-bitcoin",
          "staked-ether",
          "chainlink",
        ];

        const filteredData = data.filter(
          (coin) => !excludedCoins.includes(coin.id)
        );

        let outperformingCount = 0;
        const totalAltcoins = filteredData.length;

        // Verifică performanța fiecărei monede față de Bitcoin pe perioada de 90 de zile
        for (let coin of filteredData) {
          const bitcoin = data.find((coin) => coin.id === "bitcoin");
          if (
            coin.price_change_percentage_90d >
            bitcoin.price_change_percentage_90d
          ) {
            outperformingCount++;
          }
        }

        // Calculăm procentajul de altcoins care au performat mai bine decât BTC
        const percentage = (outperformingCount / totalAltcoins) * 100;
        setAltcoinsOutperformingBTC(percentage >= 75); // Dacă mai mult de 75% din monede au depășit BTC
      } catch (error) {
        console.error("Error fetching altcoin season data:", error);
        setAltcoinsOutperformingBTC(false);
      } finally {
        setLoading(false);
      }
    };

    fetchAltcoinSeason();
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  // Determină dacă este Altcoin Season
  const isAltcoinSeason = altcoinsOutperformingBTC;

  return (
    <div className="p-4 rounded-lg bg-gray-200 text-center">
      <h2 className="text-xl font-semibold mb-2">
        {isAltcoinSeason
          ? "It's Altcoin Season (75% of top 50 coins outperforming BTC)"
          : "It's not Altcoin Season."}
      </h2>

      <div className="w-full bg-gray-300 h-4 rounded-full">
        <div
          className="h-4 rounded-full"
          style={{
            width: `${isAltcoinSeason ? 100 : 0}%`,
            backgroundColor: isAltcoinSeason ? "#23d996" : "#ff4444",
          }}
        ></div>
      </div>

      <p className="mt-2 text-lg">
        {isAltcoinSeason
          ? "75% of altcoins have performed better than Bitcoin over the last 90 days"
          : "Less than 75% of altcoins have outperformed Bitcoin"}
      </p>
    </div>
  );
};

export default AltcoinSeason;
