import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from "recharts";

const AltcoinChart = ({ coin, onClose }) => {
  const [chartData, setChartData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchHistoricalData = async () => {
      try {
        const response = await fetch(
          `https://api.coingecko.com/api/v3/coins/${coin.id}/market_chart?vs_currency=usd&days=7`
        );
        const data = await response.json();

        // Transformă datele primite într-un format potrivit pentru Recharts
        const formattedData = data.prices.map((priceData) => ({
          date: new Date(priceData[0]).toLocaleDateString("en-GB", {
            day: "2-digit",
            month: "2-digit",
          }), // Format: DD/MM
          price: priceData[1].toFixed(2), // Prețul cu 2 zecimale
        }));

        // Folosim doar ultimele 7 puncte de date
        setChartData(formattedData.slice(-7));
      } catch (error) {
        console.error("Error fetching historical data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchHistoricalData();
  }, [coin.id]);

  if (loading) {
    return (
      <motion.div
        initial={{ opacity: 0, x: 50 }}
        animate={{ opacity: 1, x: 0 }}
        exit={{ opacity: 0, x: 50 }}
        className="fixed top-0 right-0 h-full w-1/3 bg-white dark:bg-gray-800 shadow-lg p-6"
      >
        <button
          onClick={onClose}
          className="text-gray-700 dark:text-white mb-4"
        >
          Close
        </button>
        <p className="text-gray-700 dark:text-gray-300">Loading chart...</p>
      </motion.div>
    );
  }

  return (
    <motion.div
      initial={{ opacity: 0, x: 50 }}
      animate={{ opacity: 1, x: 0 }}
      exit={{ opacity: 0, x: 50 }}
      className="fixed top-0 right-0 h-full w-1/3 bg-white dark:bg-gray-800 shadow-lg p-6"
    >
      <button onClick={onClose} className="text-gray-700 dark:text-white mb-4">
        Close
      </button>
      <h2 className="text-2xl font-bold text-gray-900 dark:text-white mb-4">
        {coin.name}
      </h2>
      <p className="text-gray-700 dark:text-gray-300 mb-6">
        Price Change: {coin.priceChange > 0 ? "+" : ""}
        {coin.priceChange.toFixed(2)}%
      </p>
      <img
        src={coin.image}
        alt={coin.name}
        className="w-16 h-16 rounded-full mb-6"
      />

      {/* Graficul */}
      <div className="w-full h-64">
        <ResponsiveContainer width="100%" height="100%">
          <LineChart data={chartData}>
            <CartesianGrid strokeDasharray="3 3" stroke="#eee" />
            <XAxis
              dataKey="date"
              tick={{
                fill: "#666",
                fontSize: 12,
                angle: -45,
                textAnchor: "end",
              }} // Rotirea etichetelor
              interval={2} // Afișează toate etichetele
              axisLine={{ stroke: "#666" }}
            />
            <YAxis
              tickFormatter={(value) => `$${value}`}
              tick={{ fill: "#666", fontSize: 12 }}
              axisLine={{ stroke: "#666" }}
            />
            <Tooltip
              contentStyle={{
                backgroundColor: "#fff",
                border: "1px solid #ddd",
                borderRadius: "4px",
              }}
            />
            <Legend />
            <Line
              type="monotone"
              dataKey="price"
              stroke="#8884d8"
              strokeWidth={3}
              dot={{ r: 4 }}
              activeDot={{ r: 8 }}
            />
          </LineChart>
        </ResponsiveContainer>
      </div>
    </motion.div>
  );
};

export default AltcoinChart;
