import React from "react";

const SentimentGauge = ({ value }) => {
  const normalizedValue = Math.min(100, Math.max(0, value)); // Asigură că valoarea rămâne între 0 și 100
  const hue = (normalizedValue / 100) * 120; // 0 (roșu) → 120 (verde)
  const color = `hsl(${hue}, 100%, 50%)`;

  return (
    <div className="flex flex-col items-center">
      <div className="relative w-56 h-56">
        <svg className="w-full h-full" viewBox="0 0 100 100">
          {/* Fundal gri */}
          <circle
            cx="50"
            cy="50"
            r="45"
            fill="#e5e7eb"
            className="dark:fill-gray-700"
          />
          {/* Cercul colorat */}
          <circle cx="50" cy="50" r="40" fill={color} />
        </svg>
        {/* Valoarea indexului (ex: "15") */}
        <span className="absolute inset-0 flex items-center justify-center text-4xl font-bold text-black dark:text-white">
          {value}
        </span>
      </div>
    </div>
  );
};

export default SentimentGauge;
