// src/components/SentimentChart.js
import React, { useEffect, useState } from "react";
import { Line } from "react-chartjs-2";
import axios from "axios";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";
import { format } from "date-fns"; // Importăm funcția de formatare a datelor

// Înregistrăm componentele Chart.js
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

function SentimentChart() {
  const [sentimentData, setSentimentData] = useState({
    labels: [], // Aici vom pune datele formate
    datasets: [
      {
        label: "Sentiment Trend (Fear & Greed Index)",
        data: [], // Aici vor fi valorile sentimentului
        fill: false,
        borderColor: "rgb(75, 192, 192)",
        tension: 0.1,
      },
    ],
  });

  const fetchSentimentData = async () => {
    try {
      // Obținem datele istorice de un an
      const response = await axios.get(
        "https://api.alternative.me/fng/?limit=365&format=json" // URL-ul pentru Fear and Greed Index pe 1 an
      );

      // Verificăm ce răspuns primim de la API
      console.log(response.data);

      // Extragem datele de sentiment pentru Fear and Greed Index
      const sentimentScores = response.data.data.map((item) => item.value); // Valorile sentimentului
      const sentimentDates = response.data.data.map(
        (item) => format(new Date(item.timestamp), "MMM dd, yyyy") // Formatarea timestamp-urilor
      ); // Acum folosim 'sentimentDates' în loc de 'sentimentTimestamps'

      console.log(sentimentScores); // Verificăm valorile sentimentului
      console.log(sentimentDates); // Verificăm datele formate

      setSentimentData({
        labels: sentimentDates, // Folosim sentimentDates pe axa X
        datasets: [
          {
            label: "Sentiment Trend (Fear & Greed Index)",
            data: sentimentScores, // Valorile de sentiment pe axa Y
            fill: false,
            borderColor: "rgb(75, 192, 192)",
            tension: 0.1,
          },
        ],
      });
    } catch (error) {
      console.error("Error fetching sentiment data:", error);
    }
  };

  useEffect(() => {
    fetchSentimentData();
  }, []);

  // Configurarea opțiunilor pentru grafic
  const options = {
    scales: {
      x: {
        title: {
          display: true,
          text: "Date", // Eticheta axei X este acum "Date"
        },
        ticks: {
          autoSkip: true,
          maxTicksLimit: 10, // Limităm numărul de etichete pe axa X
        },
      },
      y: {
        title: {
          display: true,
          text: "Fear and Greed Index",
        },
        ticks: {
          min: 0,
          max: 100,
          stepSize: 10,
        },
      },
    },
  };

  return (
    <section className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-16">
      <h2 className="text-3xl font-semibold text-gray-800">
        Market Sentiment (Fear and Greed Index)
      </h2>
      <div className="mt-6">
        <Line data={sentimentData} options={options} />
      </div>
    </section>
  );
}

export default SentimentChart;
