// src/components/Header.js
import React from "react";
import { FaSun, FaMoon } from "react-icons/fa"; // Importă iconițele

function Header({ toggleDarkMode, isDarkMode }) {
  return (
    <header className="bg-gradient-to-r from-blue-600 to-indigo-700 text-white py-10 shadow-lg">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center flex justify-between items-center">
        <h1 className="text-5xl font-extrabold tracking-tight text-transparent bg-clip-text bg-gradient-to-r from-teal-300 to-green-400">
          Crypto News and Sentiment Analysis
        </h1>
        <button
          onClick={toggleDarkMode} // Apelează funcția toggleDarkMode
          className="p-2 rounded-md bg-gray-200 dark:bg-gray-700 text-gray-900 dark:text-white"
        >
          {isDarkMode ? <FaMoon size={24} /> : <FaSun size={24} />}
        </button>
      </div>
    </header>
  );
}

export default Header;
