// src/App.js
import React from "react";
import Header from "./components/Header";
import SentimentChart from "./components/SentimentChart";
import NewsCard from "./components/NewsCard";
import AltcoinSeason from "./components/AltcoinSeason";
import Footer from "./components/Footer";
import DayNightToggle from "./components/DayNightToggle"; // Importă DayNightToggle

function App() {
  const [isDarkMode, setIsDarkMode] = useState(false);

  return (
    <div className="App bg-gray-100 min-h-screen dark:bg-gray-900 dark:text-white">
      <Header />
      <div className="absolute top-4 right-4">
        <DayNightToggle isDarkMode={isDarkMode} setIsDarkMode={setIsDarkMode} />
      </div>
      <SentimentChart />
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
        <AltcoinSeason />
        <NewsCard />
      </main>
      <Footer />
    </div>
  );
}

export default App;
