import React, { useEffect, useState } from "react";
import axios from "axios";

function App() {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(true);

  // Funcția pentru a obține știri
  const getNews = async () => {
    try {
      const response = await axios.get(
        `https://newsapi.org/v2/everything?q=crypto&apiKey=b4aeb5d2f71d497a8140ed67aed1037c`
      );
      // Verifică dacă data.articles există și este un array
      if (response.data.articles) {
        setNews(response.data.articles); // Setează știrile în state
      } else {
        setNews([]); // Dacă nu sunt știri, setează un array gol
      }
      setLoading(false); // Schimbă starea de încărcare
    } catch (error) {
      console.error("Error fetching news:", error);
      setNews([]); // În cazul unui error, setează un array gol
      setLoading(false);
    }
  };

  useEffect(() => {
    getNews(); // Apelăm funcția pentru a aduna știrile
  }, []);

  if (loading) return <div>Loading news...</div>;

  return (
    <div className="App">
      <h1>Crypto News and Sentiment Analysis</h1>
      {news.length === 0 ? (
        <p>No news found.</p>
      ) : (
        news.map((article, index) => (
          <div key={index} className="news-card">
            <h2>{article.title}</h2>
            <p>{article.description}</p>
            <a href={article.url} target="_blank" rel="noopener noreferrer">
              Read more
            </a>
          </div>
        ))
      )}
    </div>
  );
}

export default App;
