import React, { useState, useEffect } from "react";
import NightToggle from "./NightToggle"; // Importă componenta NightToggle

function Header({ setActiveTab }) {
  const [cryptoData, setCryptoData] = useState({
    bitcoinPrice: 0,
    ethereumPrice: 0,
    bitcoinDominance: 0,
    totalVolume: 0,
  });

  useEffect(() => {
    // Funcție pentru a obține prețurile Bitcoin și Ethereum
    const fetchPrices = async () => {
      try {
        const response = await fetch(
          "https://api.coingecko.com/api/v3/simple/price?ids=bitcoin,ethereum&vs_currencies=usd"
        );
        const data = await response.json();
        const bitcoinPrice = data.bitcoin.usd;
        const ethereumPrice = data.ethereum.usd;

        // Actualizează state-ul cu noile prețuri
        setCryptoData((prevState) => ({
          ...prevState,
          bitcoinPrice,
          ethereumPrice,
        }));
      } catch (error) {
        console.error("Error fetching prices:", error);
      }
    };

    // Funcție pentru a obține datele globale (dominanța Bitcoin și volumul total)
    const fetchGlobalData = async () => {
      try {
        const response = await fetch("https://api.coingecko.com/api/v3/global");
        const data = await response.json();
        const bitcoinDominance = data.data.market_cap_percentage.btc;
        const totalVolume = data.data.total_volume.usd;

        // Actualizează state-ul cu noile date globale
        setCryptoData((prevState) => ({
          ...prevState,
          bitcoinDominance,
          totalVolume,
        }));
      } catch (error) {
        console.error("Error fetching global data:", error);
      }
    };

    // Apelează ambele funcții
    fetchPrices();
    fetchGlobalData();
  }, []);

  return (
    <header className="bg-gradient-to-r from-gray-800 to-gray-900 text-white py-6 shadow-lg">
      <div className="px-4 sm:px-6 lg:px-8">
        {/* Titlul site-ului, navbar-ul și NightToggle pe aceeași linie */}
        <div className="flex items-center justify-between">
          {/* Partea din stânga: Titlul și navbar-ul */}
          <div className="flex items-center">
            {/* Titlul site-ului */}
            <h1 className="text-2xl font-extrabold tracking-tight text-transparent bg-clip-text bg-gradient-to-r from-teal-300 to-green-400">
              SentimentX
            </h1>

            {/* Navbar */}
            <nav className="flex space-x-8 text-lg font-semibold ml-8">
              <button
                className="text-white hover:text-teal-300 transition duration-300"
                onClick={() => setActiveTab("news")}
              >
                News
              </button>

              <button
                className="text-white hover:text-teal-300 transition duration-300"
                onClick={() => setActiveTab("altcoin")}
              >
                Altcoin Season Index
              </button>

              <button
                className="text-white hover:text-teal-300 transition duration-300"
                onClick={() => setActiveTab("sentiment")}
              >
                Sentiment Trend
              </button>

              <button
                className="text-white hover:text-teal-300 transition duration-300"
                onClick={() => setActiveTab("whaleTransactions")}
              >
                Whale Transactions
              </button>
            </nav>
          </div>

          {/* Partea din dreapta: NightToggle */}
          <div className="flex items-center">
            <NightToggle />
          </div>
        </div>

        {/* Linie orizontală */}
        <hr className="border-gray-700 my-4" />

        {/* Informații despre crypto */}
        <div className="flex space-x-8 text-sm font-medium text-gray-300">
          <span>BTC: ${cryptoData.bitcoinPrice.toLocaleString()}</span>
          <span>ETH: ${cryptoData.ethereumPrice.toLocaleString()}</span>
          <span>BTC Dominance: {cryptoData.bitcoinDominance.toFixed(2)}%</span>
          <span>24h Vol: ${cryptoData.totalVolume.toLocaleString()}</span>
        </div>
      </div>
    </header>
  );
}

export default Header;
