import React, { useEffect, useState } from "react";

const WhaleTransactions = () => {
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchWhaleTransactions = async () => {
      const API_KEY = "API_TĂU_KEY"; // Înlocuiește cu API-ul tău Etherscan
      const url = `https://api.etherscan.io/api?module=account&action=txlist&address=0x0000000000000000000000000000000000000000&startblock=0&endblock=99999999&page=1&offset=10&sort=desc&apikey=${API_KEY}`;

      try {
        const response = await fetch(url);
        const data = await response.json();
        if (data.status === "1") {
          setTransactions(data.result);
        } else {
          console.error("Nu s-au găsit tranzacții");
        }
      } catch (error) {
        console.error("Eroare la obținerea tranzacțiilor:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchWhaleTransactions();
  }, []);

  return (
    <div>
      <h2>Whale Transactions</h2>
      {loading ? (
        <p>Încărcăm tranzacțiile...</p>
      ) : (
        <ul>
          {transactions.map((tx) => (
            <li key={tx.hash}>
              <p>
                {tx.from} a transferat {tx.value / 10 ** 18} ETH către {tx.to}
              </p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default WhaleTransactions;
