import React, { useEffect, useState } from "react";
import axios from "axios";

function App() {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(true);

  // Funcția pentru a obține știri
  const getNews = async () => {
    try {
      const response = await axios.get(
        `https://newsapi.org/v2/everything?q=crypto&apiKey=b4aeb5d2f71d497a8140ed67aed1037c`
      );
      if (response.data.articles) {
        setNews(response.data.articles); // Setează știrile în state
      } else {
        setNews([]); // Dacă nu sunt știri, setează un array gol
      }
      setLoading(false); // Schimbă starea de încărcare
    } catch (error) {
      console.error("Error fetching news:", error);
      setNews([]); // În cazul unui error, setează un array gol
      setLoading(false);
    }
  };

  useEffect(() => {
    getNews(); // Apelăm funcția pentru a aduna știrile
  }, []);

  if (loading)
    return <div className="text-center text-2xl mt-20">Loading news...</div>;

  return (
    <div className="App bg-gray-100 min-h-screen">
      {/* Header */}
      <header className="bg-blue-600 text-white py-6">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
          <h1 className="text-4xl font-bold">
            Crypto News and Sentiment Analysis
          </h1>
        </div>
      </header>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
        {news.length === 0 ? (
          <p className="text-center text-xl text-gray-600">No news found.</p>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
            {news.map((article, index) => (
              <div
                key={index}
                className="news-card bg-white p-6 rounded-lg shadow-lg hover:shadow-xl transition"
              >
                <h2 className="text-xl font-semibold text-gray-800">
                  {article.title}
                </h2>
                <p className="mt-2 text-gray-600">{article.description}</p>
                <a
                  href={article.url}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="mt-4 inline-block text-blue-600 hover:text-blue-800"
                >
                  Read more
                </a>
              </div>
            ))}
          </div>
        )}
      </main>

      {/* Footer */}
      <footer className="bg-blue-600 text-white py-6 text-center">
        <p>&copy; 2025 Crypto Sentiment. Toate drepturile rezervate.</p>
      </footer>
    </div>
  );
}

export default App;
