// src/App.js
import React, { useState } from "react";
import Header from "./components/Header";
import SentimentChart from "./components/SentimentChart";
import NewsCard from "./components/NewsCard";
import AltcoinSeason from "./components/AltcoinSeason";
import Footer from "./components/Footer";
import DayNightToggle from "./components/DayNightToggle"; // Importă DayNightToggle

function App() {
  const [isDarkMode, setIsDarkMode] = useState(false);

  // Funcția pentru schimbarea temei
  const toggleDarkMode = () => {
    setIsDarkMode((prevMode) => !prevMode); // Comută starea temei
  };

  return (
    <div className={`App ${isDarkMode ? "dark" : ""} bg-gray-100 min-h-screen`}>
      {/* Transmite funcția toggleDarkMode și isDarkMode la Header */}
      <Header toggleDarkMode={toggleDarkMode} isDarkMode={isDarkMode} />
      <SentimentChart />
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
        <AltcoinSeason />
        <NewsCard />
      </main>
      <Footer />
    </div>
  );
}

export default App;
